if ($_POST && isset($_POST) && $zbp->VerifyCSRFToken( $_POST[ 'csrfToken' ] ) ) {      	   		     		  		 
	     			 					 	       	 	    
    if ($_GET && isset($_GET['type'])) {     		  	      				 			
        if ($_GET['type'] == 'add') {           	    			  	  
            if($zbp->Config('Jz52_Cofive')->HasKey('slidesArray')){$slidesArray = json_decode($zbp->Config('Jz52_Cofive')->slidesArray,true);}     				       		  				
            $slidesArray[] = $_POST;     	 			 	       				 
            foreach ($slidesArray as $key => $row) {     	 		 	       			 	 
                $order[$key] = $row['order'];      			 	      	  			 
                $title[$key]  = $row['title'];      		 		     	 	 	 		
            }    		   	 	     			    
            array_multisort($order, SORT_ASC, $title, SORT_DESC, $slidesArray);      	   	      				   
            $zbp->Config('Jz52_Cofive')->slidesArray = json_encode($slidesArray);    	     	     	 	 	 	 
            $zbp->SaveConfig('Jz52_Cofive');    	  			      	  		  	
        } elseif ($_GET['type'] == 'edit') {       				     	 			   
            $slidesArray = json_decode($zbp->Config('Jz52_Cofive')->slidesArray,true);      				              
            $editid = $_POST['editid'];     	 			 	      	 		 	
            unset($_POST['editid']);      		  	     	 		    
            $slidesArray[$editid] =$_POST;      		 	 	     	 	   	
            foreach ($slidesArray as $key => $row) {    	  				          			
                $order[$key] = $row['order'];    				 	        	     
                $title[$key]  = $row['title'];    	   	 	      		 	 		
            }     			 		     		     	
            array_multisort($order, SORT_ASC, $title, SORT_DESC, $slidesArray);    		 				      		   	 
            $zbp->Config('Jz52_Cofive')->slidesArray = json_encode($slidesArray);     					 	        				
            $zbp->SaveConfig('Jz52_Cofive');    	  					     				  	
        }    	  				       		 			
    }     		 		 	    	   			 
} elseif ($_GET && isset($_GET)) {    	    			    	    	  
    if ($_GET['type'] == 'del') {     				  	    	   		 	
        $slidesArray = json_decode($zbp->Config('Jz52_Cofive')->slidesArray,true);        		       		 			 
        $editid = $_GET['id'];       		 		    	   			 
        unset($slidesArray[$editid]);    	 	 	       	  	 	  
