#判断手机端    	 		 			
function Jz52_Cofive_is_mobile() {     			 	 	
	global $zbp;     			 	 	
	if (zbp_is_mobile()){    	 		    
	    $is_mobile = true;    		  				
	} else {    	 	 				
		$is_mobile = false;     			 			
	}    	  			  
	return $is_mobile;     	  		  
}    		  			 
    	 		   	
#缩略图    	 						
function Jz52_Cofive_IMG( $article, $w=400, $h=300 ) {     	      
  global $zbp;    	 	 		  
  $noimg = $zbp->host.'zb_users/theme/Jz52_Cofive/style/images/nimg.png';    	       
  if($article->Metas->Jzzd1){    			  			
	if( $zbp->Config( 'Jz52_Cofive' )->metThumb ){     	    		
		$img = Jz52_Cofive_Metas_Thumb( $article->Metas->Jzzd1, $w, $h );        	   
	}else{    	 	 			 
		$img = $article->Metas->Jzzd1;    	      	
	}      		 		 
  }else{       			 	
	  if ( $zbp->Config( 'Jz52_Cofive' )->imgthumb ) {    			   		
		$Thumb = $article->Thumbs( $w, $h, 1 );        			 
		if ( isset( $Thumb[ 0 ] ) ) {    			  		 
		  $img = $Thumb[ 0 ];    	 	    	
		} else {    	 	 	 		
		  $img = $noimg;     			 	  
		}     	 	 		 
	  } else {    	 			 		
		if ( $article->ImageCount ) {      					 
		  $img = $article->AllImages[ 0 ];     		 		 	
		} else {      	   	 
		  $img = $noimg;    	 	   	 
		}    	   	  	
	  }      	 				
  }    		 	  		
  return $img;    						 	
}    		 		  	
        				
#缩略图生成，使用ZBP1.7原生函数 php5.3    	 	  		 
function Jz52_Cofive_Metas_Thumb($img, $w, $h) {    	 	 	 	     	   	 	 
	global $zbp;       					
	    $bmd=array('gif','jpg','png','jpeg');//白名单数组      				  
	    $hz=pathinfo($img, PATHINFO_EXTENSION);//获取图片后缀    		 		 		
        if(in_array($hz,$bmd)){    	 		 			
		    $imgs = array();;     	 					
		    $imgs[0] = $img;        			 
			$imgarr = Thumb::Thumbs($imgs, $w, $h, 1, true);    		 	 	  
		    return $imgarr[0];      				  
        }else{      	   	 
            return $img;     	    	 
        }      	 		 	
}         	  
     	  		 	
#分类输出 改进 如果存在下级则输出下级 如果不存在，则输出二级，顶级分类名链接到顶级    		   	  
function Jz52_Cofive_subCaten($CateID,$CateUrl){       	   	
	global $zbp;	    			  	  
	if($CateID) {    	  				 
		if($zbp->GetCategoryByID($CateID)->RootID || $zbp->GetCategoryByID($CateID)->SubCategorys){//只有存在顶级分类或子分类才输出    		      
			$qID = $CateID;    	 		 			
			if($zbp->GetCategoryByID($CateID)->RootID){    				 			
				$qID = $zbp->GetCategoryByID($CateID)->RootID; //顶级目录id     	  		 	
			}    			 			 
			if($zbp->GetCategoryByID($CateID)->RootID && !$zbp->GetCategoryByID($CateID)->SubCategorys){ //不存在子分类则    		  				
				$CateID = $zbp->GetCategoryByID($CateID)->RootID;        			 	
			}    							 
			echo '<div class="nydh"><div class="title"><p><a href="'.$zbp->categorys[$qID]->Url.'" title="'.$zbp->categorys[$qID]->Name.'" >— '.$zbp->categorys[$qID]->Name.' —</a></p></div><ul class="ul">';    			  			
			foreach ($zbp->GetCategoryByID($CateID)->SubCategorys as $cate) {       	 		 
			$hover='';     		     
				if($CateUrl==$cate->Url){      			 		
					$hover=' class="on"';    		   		 
				}     			 	  
				echo '<li'.$hover.'><a href="'.$cate->Url.'" title="'.$cate->Name.'">'.$cate->Name.'</a>';				     			 		 
			}    		  		  
			echo '</ul></div>';    	  				 
		}else{     		 	 		
		    if($zbp->Config('Jz52_Cofive')->flxsdy){    		   	  
		        echo '<div class="nydh"><div class="title"><p><a href="'.$zbp->categorys[$CateID]->Url.'" title="'.$zbp->categorys[$CateID]->Name.'">— '.$zbp->categorys[$CateID]->Name.' —</a></p></div><ul class="ul">'.$zbp->Config('Jz52_Cofive')->clnav.'</ul></div>';      	 	  	
		    }    	    		 
		}       					
	}      	   		
}    	 					 
     	 		   
